    private void btn00ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn00ActionPerformed
        // TODO add your handling code here
        if(turn%2==0){
            //p1 code
            btn00.setEnabled(false);
            btn00.setText("o");
            board[0][0]='o';
            if(checkwinner('o')){
                lockBoard();
                p1score++;
                lblPlayer1Score.setText("p1 score :"+p1score);
                        
            }
            turn++;
            lblPlayerPlaying.setText("Player Playing : "+((turn%2)+1));
        }
        else{
            //p2 code
             btn00.setEnabled(false);
            btn00.setText("x");
            board[0][0]='x';
            if(checkwinner('x')){
                lockBoard();
                p2score++;
                lblPlayer2Score.setText("p2 score :"+p2score);
                        
            }
            turn++;
            lblPlayerPlaying.setText("Player Playing : "+((turn%2)+1));
        }
    }//GEN-LAST:event_btn00ActionPerformed

