    void lockBoard()
    {
        for(int i=0;i<9;i++)
        {
            pnlBoard.getComponent(i).setEnabled(false);
        }
    }
    
    void unlockBoard(){
       for(int i=0;i<9;i++)
        {
            pnlBoard.getComponent(i).setEnabled(true);
        } 
       btn00.setText("");
       btn10.setText("");
       btn01.setText("");
       
       btn02.setText("");
       btn11.setText("");
       btn12.setText("");
       
       btn20.setText("");
       btn21.setText("");
       btn22.setText("");
       btn00.setText("");
       
       turn = 0;
       for(int i=0;i<3;i++){
           for(int j=0;j<3;j++){
            board[i][j]=' ';
       }
    }
    }
    

    boolean checkwinner(char symbol){
        for(int i=0;i<3;i++){
            // row winner check
            if((board[i][0]==symbol)&&(board[i][1]==symbol)&&(board[i][2]==symbol)){
                return true;
            }
            // column winner check
            if((board[0][i]==symbol)&&(board[1][i]==symbol)&&(board[2][i]==symbol)){
                return true;
            }
        }
        // left top diagonal
        if((board[0][0]==symbol)&&(board[1][1]==symbol)&&(board[2][2]==symbol)){
                return true;
        }
        //top right
        if((board[0][2]==symbol)&&(board[1][1]==symbol)&&(board[2][0]==symbol)){
                return true;
        }
        return false;
    }
    
 
    char [][]board = new char[3][3];
    int turn=0;
    int p1score=0;
    int p2score=0;

